    <script type="text/javascript">
        window.debounce = function(fn, delay) {
            let timer;
            return (...args) => {
                clearTimeout(timer);
                timer = setTimeout(() => fn(...args), delay);
            };
        }
        window.createVendorWatcher = function(vendorId, callback) {
            let previousStatus = null;
            // Initialiser l'état
            window.didomiOnReady = window.didomiOnReady || [];
            window.didomiOnReady.push(function () {
                previousStatus = Didomi.getUserConsentStatusForVendor(vendorId);
            });

            // Écouter les changements
            window.didomiEventListeners = window.didomiEventListeners || [];
            window.didomiEventListeners.push({
                event: 'consent.changed',
                listener: function(context) {
                    const currentStatus = Didomi.getUserConsentStatusForVendor(vendorId);

                    if (currentStatus !== previousStatus) {
                        callback({
                            vendorId: vendorId,
                            oldStatus: previousStatus,
                            newStatus: currentStatus,
                            context: context
                        });
                        previousStatus = currentStatus;
                    }
                }
            });
        }
        window.runAfterDidomiReady = function (callback) {
            if (window.didomiIsReady) {
                setTimeout(() => {
                    callback();
                }, 0);
            } else {
                window.startAfterDidomi = window.startAfterDidomi || [];
                window.startAfterDidomi.push(callback);
            }
        }
